void Levels::game_over_screen(Player& player)
{
	completed = false;
	while (!game_over)
	{
		BeginDrawing();
		ClearBackground(BLACK);
		DrawTextEx(font, "Game Over", game_over_pos, 150, 10, color);
		if (color.a < 255 && !swap)
		{
			if (color.a + 2 > 255)
				color.a--;
			color.a += 2;
		}
		if (color.a == 255)
			swap = true;
		if (swap)
		{
			if (color.a - 2 < 0)
				color.a++;
			color.a -= 2;
		}
		if (color.a == 0)
		{
			swap = false;
			game_over = true;
		}
		EndDrawing();
	}
}
void Levels::manage_death(Player& player, Enemies& enemies)
{
	if (player.death)
	{
		game_over = false;
		PlaySound(player.death_sound);
		player.lives--;
		if (player.lives <= 0)
			game_over_screen(player);
		player.set_pos3();
		enemies.reset_enemies();
		player.speed.x = 7;
		player.death = false;
	}
}
void Levels::reset_music()
{
	StopMusicStream(level1_music);
	StopMusicStream(level2_music);
	PlayMusicStream(level1_music);
	PlayMusicStream(level2_music);
}

bool Levels::CheckCollisionRecsOr(Rectangle rec1, Rectangle rec2, Vector2 origin)
{
	return 
		rec1.x + rec1.width > rec2.x - origin.x && rec1.x < rec2.x + rec2.width - 75 && 
		rec1.y + rec1.height > rec2.y - origin.y && rec1.y < rec2.y + rec2.height - 75;
}